import { useState } from "react";
import "./App.css";
import Decremento from "./Components/ComponeneteDecremento/Decremento";
import Incremento from "./Components/ComponeneteIncremento/Incremento";

function App() {
const [contador, setContador] = useState(0);
  let handler = (e) => {
    switch (e.target.id) {
      case "btn_incremento":
        setContador(contador+1);
        break;
      case "btn_decremento":
        setContador(contador-1);
        break;
      case "btn_reset":
        setContador(0);
        break;

      default:
        break;
    }
  };

  return (
    <div className="container m-4">
      <button
        className="btn btn-primary"
        name="btn_incremento"
        onClick={handler}
      >
        +1
      </button>
      <button
        className="btn btn-primary"
        name="btn_decremento"
        onClick={handler}
      >
        -1
      </button>
      <button className="btn btn-primary" name="btn_reset" onClick={handler}>
        Reset
      </button>
      <div className="row m-4">
        <div className="col">
          <Incremento counter={contador}/>
        </div>
        <div className="col">
          <Decremento counter={contador}/>
        </div>
      </div>
    </div>
  );
}

export default App;
