import { useState } from "react";
import "./App.css";
import Decremento from "./Components/ComponeneteDecremento/Decremento";
import Incremento from "./Components/ComponeneteIncremento/Incremento";

function App() {
let contador = 0;
const [first, setfirst] = useState(second)
  let handler = (e) => {
    switch (e.target.id) {
      case "btn_incremento":
        contador++;
        break;
      case "btn_decremento":
        contador--;
        break;
      case "btn_reset":
        contador=0;
        break;

      default:
        break;
    }
  };

  return (
    <div className="container m-4">
      <button
        className="btn btn-primary"
        name="btn_incremento"
        onClick={handler}
      >
        +1
      </button>
      <button
        className="btn btn-primary"
        name="btn_decremento"
        onClick={handler}
      >
        -1
      </button>
      <button className="btn btn-primary" name="btn_reset" onClick={handler}>
        Reset
      </button>
      <h1> {contador}</h1>
      <div className="row m-4">
        <div className="col">
          <Incremento />
        </div>
        <div className="col">
          <Decremento />
        </div>
      </div>
    </div>
  );
}

export default App;
