import './ComponenteEventos.css'

function ComponenteEventos() {
    let contenido = "Este es el contenido";
    let gestionBoton=()=>{
        alert(contenido)
        console.log("Boton pulsado por metodo gestion");
    }
    let gestionTeclas = (e)=>{
        contenido = e.target.value;
        console.log(contenido);
    }
    return(
        <div>
            <h2>Componente Eventos</h2>
            <input className="form-control" onKeyUp={gestionTeclas} type="text" placeholder="Introduce tu nombre"></input>
            <input className="form-control mb-4" type="nombre" placeholder="Introduce tu edad"></input>
            <button className="btn btn-primary"
            onClick={
                gestionBoton
            }
            >Pulsar a√±adir</button>
            <button className="btn btn-primary"
            onClick={()=>{
                    console.log('boton pulsado');
            }}
            >Pulsar Borrar</button>
        </div>
    );
}
export default ComponenteEventos;
