import React, { useState } from 'react';



function ComponenteState() {
    const [elementos, setElementos] = useState([]);
    const [elementoInput, setElementoInput] = useState("");
    const [correo, setCorreo] = useState("");
    const [contraseñoa, setcontraseñoa] = useState(second)
    return (
        <div>
            <h2>Componente State</h2>
            <div className='m-4'>
                <input type="text" className="form-control"

                    onKeyUp={(e) => {
                        setElementoInput(e.target.value)
                    }}
                    placeholder='introduce algo'></input>
                <button className='btn btn-primary'
                    onClick={() => {
                        setElementos((e) => {
                            return [...e, elementoInput];
                        });

                    }}
                >Enviar</button>
                <button
                    onClick={() => {
                        console.log(elementos);
                    }}
                >Comprobar</button>
            </div>
            <ul>
                {elementos.map(d => <li>{d}</li>)}
            </ul>

            <div className="mb-3">
                <label for="exampleInputEmail1" className="form-label">Email address</label>
                <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp"></input>
                <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
            </div>
            <div className="mb-3">
                <label for="exampleInputPassword1" className="form-label">Password</label>
                <input type="password" className="form-control" id="exampleInputPassword1"></input>
            </div>
            <div className="mb-3 form-check">
                <input type="checkbox" className="form-check-input" id="exampleCheck1"></input>
                <label className="form-check-label" for="exampleCheck1">Check me out</label>
            </div>
            <button type="submit" className="btn btn-primary">Submit</button>

        </div>
    );
}
export default ComponenteState;
