import "./App.css";

function App() {
  let variableIf = true;
  let numeroIf = 10;
  let numeroSwitch=4;
  let elementos={}

  function crearHeader(numero) {
    switch (numero) {
      case 1:
        return <p>Este parrafo aparece si me pasas 1</p>;
      case 2:
        return <p>Este parrafo aparece si me pasas 2</p>;
      case 3:
        return <p>Este parrafo aparece si me pasas 3</p>;
      default:
        return <p>Este parrafo aparece si me pasas otra cosa</p>;
    }
  }
  function switchF(numeroSwitch){
    switch (numeroSwitch) {
      case 1:
        return <div className="alert alert-primary" role="alert">
        primera opcion
      </div>
      case 2:
        return <div className="alert alert-secundary" role="alert">
        segunda opcion
      </div>
      case 3:
        return <div className="alert alert-sucses" role="alert">
        tercera opcion
      </div>
      case 4:
        return <div className="alert alert-danger" role="alert">
        cuarta opcion
      </div>
      default:
        return <div className="alert alert-primary" role="alert">
        otra opcion
      </div>
    }
  }
  function forF(){
    for (let i = 0; i <  10; i++) {
      return <li>elemento {i}</li>

    }
  }
  return (
    <div className="App container">
      <h1>Aplicacion React</h1>
      <p> Primera aplicacion React</p>
      <div className="row">
        <div className="col columna-uno">
          <h2>Elementos de un componenete</h2>
          <h3>Sentencia if</h3>
          <div>
            {/* {variableIf && numeroIf > 10 ? (
              <div className="alert alert-primary" role="alert">
                la condicion es verdadera
              </div>
            ) : (
              <div className="alert alert-danger" role="alert">
                la condicion es falsa
              </div>
            ) */
            variableIf && numeroIf <= 10 &&(
              <div className="alert alert-primary" role="alert">
                la condicion es verdadera
              </div>
            )}
          </div>
          <h3>Sentencia Switch</h3>
          {switchF(numeroSwitch)}
          <h3>Sentencia For</h3>
          <ul>
          {forF()}
          </ul>
        </div>
        <div className="col columna-dos">
          <h2>Funcionalidad de un comoponente</h2>

          {crearHeader(1)}
        </div>
      </div>
    </div>
  );
}

export default App;
